import { useEffect, useState } from 'react';
import Answer from './Answer';

function StudyPlay({numVocab,numGrammar,vocabularyList,grammarList}) {
    const [count,setCount] = useState(1);
    const [isShowAnswer,setIsShowAnswer] = useState(false);
    // const [question,setQuestion] = useState([]);
    let question = []
    let total = numVocab + numGrammar;

    let verbTypeList = ["V-ing", "to + V1", "V1", "v3/ed","v2"];

    const formatType = (type) =>{
        if(type === "Danh từ"){
            return "(n)";
        }
        else if(type === "Động từ"){
            return "(v)";
        }
        else if(type === "Tính từ"){
            return "(adj)";
        }
        else if(type === "Trạng từ"){
            return "(adv)";
        }
    }

    useEffect(() => {

    })

    

    return ( 
        <>
        <div className="card">
            <div className="card-body p-0">
                <h5 className="card-title p-3 m-0 rounded-top bg-primary" style={{color:"#ffffff"}}>
                    <p className="col-md-1 m-0 d-inline-block">Câu hỏi:</p>
                    <div className="col-md-10 text-center d-inline-block">
                        <p className="d-inline-block m-0">{count}/{total}</p>
                    </div>
                    
                </h5>
                <div className="border-top-0 rounded-0 text-center" style={{minHeight: 80 + "px",padding: 20 + "px"}}>
                    <h3 className="m-0">{question[count-1].question}</h3>
                </div>
            </div>
        </div>
        <div className="card">
            <div className="card-body p-3">
                <div className="row m-0">
                    <div className="col-md-6 px-5 py-3">
                        <Answer key={0} index = {0} question = {question} count = {count} setCount = {setCount} isShowAnswer = {isShowAnswer} setIsShowAnswer = {setIsShowAnswer}/>
                    </div>
                    <div className="col-md-6 px-5 py-3">
                        <Answer key={1} index = {1} question = {question} count = {count} setCount = {setCount} isShowAnswer = {isShowAnswer} setIsShowAnswer = {setIsShowAnswer}/>
                    </div>
                </div>
                <div className="row m-0">
                    <div className="col-md-6 px-5 py-3">
                        <Answer key={2} index = {2} question = {question} count = {count} setCount = {setCount} isShowAnswer = {isShowAnswer} setIsShowAnswer = {setIsShowAnswer}/>
                    </div>
                    <div className="col-md-6 px-5 py-3">
                        <Answer key={3} index = {3} question = {question} count = {count} setCount = {setCount} isShowAnswer = {isShowAnswer} setIsShowAnswer = {setIsShowAnswer}/>
                    </div>
                </div>
            </div>
        </div>
        </>
     );
}

export default StudyPlay;
