import styled, {css} from 'styled-components';
import TrashIcon from '@atlaskit/icon/glyph/trash'
import Button from '@atlaskit/button';


const ButtonConfig = styled(Button)`
    background-color: white !important;
    &:hover{
        background-color: #d6d6d6!important;
    }
`;

function CourseItem({course, removeCourse}) {
    return (
        <tr id={course.id}>
            <th scope="row" className='align-middle'>{course.index}</th>
            <td className='align-middle'>{course.name}</td>
            <td className='align-middle'>
                <div className="row">
                    <div className="RemoveIcon icon col-md-6 ">
                        <ButtonConfig 
                            iconAfter= { true && (
                                <span className='WatchIcon'>
                                    <TrashIcon />
                                </span>
                                )
                            }
                            onClick={() => removeCourse(course.id)}
                        />
                    </div>
                </div>
            </td>
        </tr>
    );
}

export default CourseItem;
