import Button from '@atlaskit/button';
import styled, {css} from 'styled-components';

const ButtonConfig = styled.button`
    background-color: ${p => p.isShowAnswer? (p.isClick ? (p.isAnswer ? "blue!important":"red!important") : (p.isAnswer?"blue!important":"white!important")) : "white!important"};
    height: 100px;
    width: 100%;
    box-shadow: 1px 1px 8px;
    padding: 30px;
    border-radius: 10px;

    &:hover{
        background-color: #d6d6d6;
    }
`;

