const ButtonConfig = styled(Button)`
background-color: white!important;
height: 100px;
width:100%;
box-shadow: 1px 1px 8px;

&{
    ${(p) => 
        p.isShowAnswer && 
        css`
        background-color: #d6d6d6!important;`
    }
}

padding:30px;

&:hover{
    background-color: #d6d6d6!important;
}
`

function Answer() {
    const onClickAnswer = (answer,trueAnswer,num) =>{
        isShowAnswer = true;
        if(answer === trueAnswer){
            isRight = true;
            numClick = num
        }
    }
    return ( 
        <>
            <ButtonConfig
                isShowAnswer={isShowAnswer}
                style={isShowAnswer? (numClick === 0 ? (question[count - 1].answerList[0] === question[count - 1].trueAnswer ? showBlue : showRed):(question[count - 1].answerList[0] === question[count - 1].trueAnswer ? showBlue:noShowAnswer)) : noShowAnswer}
                onClick={() => onClickAnswer(question[count - 1].answerList[0],question[count - 1].trueAnswer,0)}
            >
                {question[count - 1].answerList[0]}
            </ButtonConfig>
        </>
    );
}

export default Answer;
