import Button from '@atlaskit/button';
import styled, {css} from 'styled-components';


// ${p => p.isShowAnswer? (p.isClick ? (p.isAnswer ? "blue!important":"red!important") : (p.isAnswer?"blue!important":"white!important")) : "white!important"};
const ButtonConfig = styled.button`
    background-color: ${p => p.isShowAnswer ? "red":"white"};
    height: 100px;
    width: 100%;
    box-shadow: 1px 1px 8px;
    padding: 30px;
    border-radius: 10px;

    &:hover{
        background-color: #d6d6d6;
    }
`;

function Answer({index,answer,onClickAnswer,isShowAnswer,numClick,trueAnswer}) {
    console.log(numClick);
    console.log(trueAnswer);
    return ( 
        <>
            <div className="col-md-6 px-5 py-3">
                <ButtonConfig
                    isShowAnswer = {isShowAnswer}
                    onClick={() => onClickAnswer(index)}
                >
                    {answer}
                </ButtonConfig>
            </div>
        </>
    );
}

export default Answer;
