import Textfield from '@atlaskit/textfield';
import Button from '@atlaskit/button';
import CourseItem from './CourseItem';
import { useCallback, useState } from 'react';
import {v4} from 'uuid' //get method v4 from uuid

function Home() {
    const [CourseList,setCourseList] = useState([])
    const [textInput,setTextInput] = useState("")

    const addCourse = useCallback((e) => {
        setCourseList([...CourseList,{id:v4(),name: textInput,vocabularyList:[],isHidden:false}])

        setTextInput("")
    },CourseList,textInput)

    const onTextInputChange = useCallback((e) => {
        setTextInput(e.target.value)
    },[])

    const checkHidden = useCallback((e) => {
        setCourseList()
    },)

    return (
        <>
            <div className="pagetitle">
                <h1>Home</h1>
                <nav>
                    <ol className="breadcrumb">
                        <li className="breadcrumb-item"><a href="index.html">Home</a></li>
                    </ol>
                </nav>
            </div>
            <section className="section dashboard">
                <div className="card">
                    <div className="card-body">
                        <h5 className="card-title" >Tạo mới Khóa học</h5>
                        <Textfield
                            placeholder='Nhập tên khóa học vào đây'
                            elemAfterInput={
                                <Button 
                                isDisabled={!textInput} 
                                appearance="primary" 
                                onClick={addCourse}
                                > 
                                  Thêm
                                </Button>
                            }
                            value={textInput}
                            onChange={onTextInputChange}
                        ></Textfield>
                    </div>
                </div>
                <div className="card">
                    <div className="card-body">
                        <div className="row align-items-center">
                            <h5 className="card-title col-md-8 mb-0" >Danh sách khóa học</h5>
                            <div className="col-md-4">
                                <div className="input-group">
                                    <input className="form-control" type="text" placeholder="Search" title="Enter search keyword" />
                                    <button type="button" className="btn btn-light"><i className="bi bi-search"></i></button>
                                </div>
                            </div>                
                        </div>
                        <table className="table datatable text-center">
                            <thead>
                                <tr>
                                    <th scope="col" style={{width: 10 + "%"}}>#</th>
                                    <th scope="col" style={{width: 80 + "%"}}>Tên khóa học</th>
                                    <th scope="col" style={{width: 10 + "%"}}></th>
                                </tr>
                            </thead>
                            <tbody>
                                <CourseItem />
                            </tbody>
                        </table>
                    </div>
                </div>
            </section>
        </>
    );
}

export default Home;
