import WatchFilledIcon from '@atlaskit/icon/glyph/watch-filled'
import WatchIcon from '@atlaskit/icon/glyph/watch'
import TrashIcon from '@atlaskit/icon/glyph/trash'
import styled, {css} from 'styled-components';
import Button from '@atlaskit/button';

const ButtonConfig = styled(Button)`
    background-color: white !important;
    &:hover{
        background-color: #d6d6d6!important;
    }
`;

function VocabularyItem({index,vocabulary,clickHiddenButton,clickRemoveButton}) {
    return ( 
        <tr id="row-1">
            <th scope="row" className='align-middle'>{index+1}</th>
            <td className='align-middle'>{vocabulary.tuvung}</td>
            <td className='align-middle'>{vocabulary.loaitu}</td>
            <td className='align-middle'>{vocabulary.nghiacuatu}</td>
            <td className='align-middle'>
                <div className="row">
                    <div className="icon col-md-6">
                        <ButtonConfig
                            iconAfter= {(
                                <span className='WatchIcon'>
                                    {vocabulary.isHidden ? (<WatchFilledIcon />) : <WatchIcon />}
                                </span>
                                )
                            }
                            isHidden = {vocabulary.isHidden}
                            onClick={() => clickHiddenButton(vocabulary.id)}
                        >
                        </ButtonConfig>
                    </div>
                    <div className="RemoveIcon icon col-md-6 ">
                        <ButtonConfig 
                            iconAfter= { true && (
                                <span className='WatchIcon'>
                                    <TrashIcon />
                                </span>
                                )
                            }
                        onClick={() => clickRemoveButton(vocabulary.id)}
                        > 
                        </ButtonConfig>
                    </div>
                </div>
            </td>
        </tr>
    );
}

export default VocabularyItem;
