import styled, {css} from 'styled-components';
import TrashIcon from '@atlaskit/icon/glyph/trash'
import Button from '@atlaskit/button';
import { useCallback, useState } from 'react';
import {v4} from 'uuid' //get method v4 from uuid

const ButtonConfig = styled(Button)`
    background-color: white !important;
    &:hover{
        background-color: #d6d6d6!important;
    }
`;

function CourseItem() {
    const [CourseList,setCourseList] = useState([])
    const [textInput,setTextInput] = useState("")

    const addCourse = useCallback((e) => {
        setCourseList([...CourseList,{id:v4(),name: textInput,vocabularyList:[],isHidden:false}])

        setTextInput("")
    },CourseList,textInput)

    return (
        <tr id="row-1">
            <th scope="row" className='align-middle'>1</th>
            <td className='align-middle'>English</td>
            <td className='align-middle'>
                <div className="row">
                    <div className="RemoveIcon icon col-md-6 ">
                        <ButtonConfig 
                            isComplete={true} 
                            iconAfter= { true && (
                                <span className='WatchIcon'>
                                    <TrashIcon />
                                </span>
                                )
                            }
                        > 
                        </ButtonConfig>
                    </div>
                </div>
            </td>
        </tr>
    );
}

export default CourseItem;
