import './App.css';
import Header from '../Header/Header';
import Navbar from '../Navbar/Navbar';
import Content from '../Content/Content';
import { useCallback, useEffect, useState } from 'react';
import {v4} from 'uuid' //get method v4 from uuid
import {
  BrowserRouter as Router,
} from "react-router-dom";

function App() {
  //create state CourseList
  const [CourseList,setCourseList] = useState([])

  // get CourseList in localStorage
  useEffect(() => {
    const localStoredCourseList = localStorage.getItem("CourseList")
    if (localStoredCourseList){
      setCourseList(JSON.parse(localStoredCourseList))
    }
  },[])

  // save CourseList to localStorage
  useEffect(() => {
    localStorage.setItem("CourseList",JSON.stringify(CourseList))
  },[CourseList])

  const addCourse = useCallback((textInput,setTextInput) => {
      setCourseList([...CourseList,{id:v4(), name: textInput, vocabularyList:[]}])
      setTextInput("")
  },[CourseList])

  const removeCourse = useCallback((id) => {
    setCourseList(prevState => prevState.filter(course => course.id != id))
  },[])

  const addVocabulary = useCallback((id,vocabularyList) => {
    setCourseList(prevState => prevState.map(course => course.id === id ? {...course,vocabularyList: vocabularyList} : course))
},[])

  const hiddenVocabulary = useCallback((course_id,vocab_id) => {
    setCourseList(prevState => prevState.map(course => course.id === course_id ))
  },[])

  return (
    <Router>
      <div className="App">
        <Header />
        <Navbar CourseList = {CourseList} />
        <Content CourseList = {CourseList} removeCourse = {removeCourse} addCourse = {addCourse} addVocabulary = {addVocabulary}/>
        <a href="" className="back-to-top d-flex align-items-center justify-content-center"><i className="bi bi-arrow-up-short"></i></a>
      </div>
    </Router>
  );
}

export default App;
