import { useCallback, useState } from "react";
import { useParams } from "react-router-dom";
import StudyPlay from "./StudyPlay";

function StudyPage() {
    const params = useParams();
    const [isPlay,setIsPlay] = useState(false);
    const [numVocab,setNumVocab] = useState(0)
    const [numGrammar,setNumGrammar] = useState(0)

    const onClickPlay = () =>{
        if(numVocab < 0 ||  numVocab > 10){
            alert("Làm ơn nhập lại số câu từ vựng!");
        }
        else if(numGrammar < 0 || numGrammar > 10){
            alert("Làm ơn nhập lại số câu ngữ pháp!");
        }
        else{
            setIsPlay(!isPlay);
        }
    }

    const onChangeNumVocab = useCallback((e) =>{
        setNumVocab(e.target.value);
    },[])

    const onChangeNumGrammar = useCallback((e) =>{
        setNumGrammar(e.target.value);
    },[])

    return (
        <>
            <div className="pagetitle">
                <h1>Study</h1>
                <nav>
                    <ol className="breadcrumb">
                        <li className="breadcrumb-item"><a href="index.html">Home</a></li>
                        <li className="breadcrumb-item">{params.lessonId}</li>
                        <li className="breadcrumb-item active">Study</li>
                    </ol>
                </nav>
            </div>
            <section className="section dashboard">
                <div className="card">
                    <div className="card-body">
                        <h5 className="card-title" >Setting</h5>
                        <div className="row">
                            <div className="col-md-5">
                                <label >Số câu từ vựng: </label>
                                <input className="m-1" type="number" min="0" max="10" onChange={onChangeNumVocab} value={numVocab}></input>
                            </div>
                            <div className="col-md-5">
                                <label >Số câu ngữ pháp: </label>
                                <input className="m-1" type="number" min="0" max="10" onChange={onChangeNumGrammar} value={numGrammar}></input>
                            </div>
                            <div className="col-md-2">
                                <button className="btn btn-primary" onClick={onClickPlay}>
                                    <i className="bi bi-plus-circle"> </i>
                                        {isPlay ? "Dừng học":"Bắt đầu"}
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
                {isPlay ? <StudyPlay /> : <div></div>}
            </section>
        </>);
}

export default StudyPage;
