import './App.css';
import Header from '../Header/Header';
import Navbar from '../Navbar/Navbar';
import Content from '../Content/Content';
import { useCallback, useEffect, useState } from 'react';
import {v4} from 'uuid' //get method v4 from uuid
import {
  BrowserRouter as Router,
} from "react-router-dom";
import axios from 'axios';

function App() {
  //create state LessonList
  const [LessonList,setLessonList] = useState([])

  // get LessonList in api server
  useEffect(() => {
    axios.get('/lesson')
          .then(res => {
            console.log(res.data);
            setLessonList(res.data);
          })
          .catch(err => console.log(err));
  },[])

  const addLesson = (textInput,setTextInput) => {
    axios.post('/lesson/create',{
      lessonName: textInput
    })
    .then(res => {
      console.log(res);
    })
    .catch(err => console.log(err));

    axios.get('/lesson')
          .then(res => {
            setLessonList(res.data);
          })
          .catch(err => console.log(err));

    setTextInput("");
  };

  const removeLesson = useCallback((id) => {
    axios.get('/lesson/destroy/' + id)
          .then(res => {
            console.log(res);
          })
          .catch(err => console.log(err));
  },[])

  return (
    <Router>
      <div className="App">
        <Header />
        <Navbar LessonList = {LessonList} />
        <Content LessonList = {LessonList} addLesson = {addLesson} removeLesson = {removeLesson} />
        <a href="" className="back-to-top d-flex align-items-center justify-content-center"><i className="bi bi-arrow-up-short"></i></a>
      </div>
    </Router>
  );
}

export default App;
