import logo from './logo.svg';
import './App.css';
import Header from '../Header/Header';
import Navbar from '../Navbar/Navbar';
import Content from '../Content/Content';
import { useCallback, useEffect, useState } from 'react';
import {v4} from 'uuid' //get method v4 from uuid

function App() {
  const [CourseList,setCourseList] = useState([])
  const [count,setCount] = useState(0)

  useEffect(() => {
    const localStoredCourseList = localStorage.getItem("CourseList")
    if (localStoredCourseList){
      setCourseList(JSON.parse(localStoredCourseList))
    }
  },[])

  useEffect(() => {
    localStorage.setItem("CourseList",JSON.stringify(CourseList))
  },[])

  const addCourse = useCallback((textInput,setTextInput) => {
      setCourseList([...CourseList,{id:v4(), name: textInput, vocabularyList:[], index: count}])
      setTextInput("")
      setCount(count + 1)
  },[CourseList,count])

  const removeCourse = useCallback((id) => {
      setCourseList(prevState => prevState.filter(course => course.id != id))
  },[])

  return (
    <div className="App">
      <Header />
      <Navbar CourseList = {CourseList} />
      <Content CourseList = {CourseList} removeCourse = {removeCourse} addCourse = {addCourse}/>

      <a href="" className="back-to-top d-flex align-items-center justify-content-center"><i className="bi bi-arrow-up-short"></i></a>
    </div>
  );
}

export default App;
